mapper.update:
update comment
set thumb_count = thumb_count + #{thumbNum}
where id = #{id}

update comment
set thumb_count = IF(thumb_count > 0, thumb_count - #{thumbNum}, 0)
where id = #{id}

mapper.select:
select * from comment


		post_id = #{commentQO.postId}


		and user_id = #{commentQO.userId}


		and type = #{commentQO.type}


		and first_comment_id = #{commentQO.firstCommentId}


		and state = #{status}

	and deleted = 0

        select * from comment


                and user_id = #{userId}


                and state = #{status}

			and deleted = 0

        group by post_id

