mapper.update:
update user_profile
set article_num = article_num + 1
where user_id = #{userId}

update user_profile
set fan_num = fan_num + 1
where user_id = #{toUserId}

update user_profile
set fan_num = fan_num - 1
where user_id = #{toUserId}

update user_profile
set follow_num = follow_num + 1
where user_id = #{userId}

update user_profile
set follow_num = follow_num - 1
where user_id = #{userId}
